import { maxWidth } from "@mui/system";
import {CardElement, useElements, useStripe } from "@stripe/react-stripe-js";
import axios from "axios"
import { CreatePayment } from "../../redux/actions/paymentAction";
import React, { useState, useEffect,  } from "react";
import { API_URL } from "../Stripe";
import { useDispatch, useSelector } from "react-redux";
import { Row ,Col, ListGroup, Image, Button} from "react-bootstrap";



const Payment = () => {
    const [message, setMessage] = useState("");
    const cart = useSelector(state => state.cart)
    const {cartItems} = cart
    console.log(cartItems[0].title)
   
    const orderCreate = useSelector(state => state.orderCreate)
    const {orderUser} = orderCreate
    console.log(orderUser?.[0]?.totalPrice)
   
    const dispatch = useDispatch()

    const handlePayment = e => {  
        e.preventDefault();
        dispatch(CreatePayment())
    }

    useEffect(() => {

        const query = new URLSearchParams(window.location.search)
        if (query.get("success")) {
            setMessage("Order placed! You will receive an email confirmation.");
          }
      
        if (query.get("canceled")) {
            setMessage(
              "Order canceled -- continue to shop around and checkout when you're ready."
            );
          }
        }, []);
    


    return(
        <Col md={8}>  
             
     

        {cartItems.length === 0 ? (<strong>Le panier est vide</strong>) : (
            <ListGroup variant="flush">
                {cartItems.map(item => 
                    <ListGroup.Item key={item.id}>
                            <Row>
                                <Col md={2}>
                                    <Image id='imageProduct' src={item.image} height={175} width={210}/>
                                </Col>
                            
                              
                                <Col md={2}>
                                     <p id="productPrice">{item.price.toFixed(2)}â‚¬</p>
                                </Col>
                              
                                <Col md={1}>
                                    <Button
                                        type='button'
                                        variant='light'

                                         

                                        >Checkout
                                            <i className="fas fa-trash"></i>
                                    </Button>
                                </Col>

                               
                            </Row>
                             
                    </ListGroup.Item>
                  
                        )}
              
            </ListGroup>
        )}
    </Col>
    )
}

export default Payment
