import React,{ Fragment, useEffect, useState} from 'react'

const CartItem = (props) =>  {

    
    const [show, setShow] = useState(true)
    const {isOpen} = props 
     
    console.log(isOpen)
    const handleClose = () => setShow(false)
    //const handleShow  = () => setShow(isOpen)
    getAuthentication = localStorage.getItem('access_token')
    console.log(getAuthentication)

    const cartViewIsAuthenticated = !getAuthentication ? (


                    <Offcanvas show={show}  onHide={handleClose} placement='end'>
                    <Offcanvas.Header closeButton>
                        <Offcanvas.Title>Cart</Offcanvas.Title>
                            <Offcanvas.Body>
                                Coucou
                            </Offcanvas.Body>
                    </Offcanvas.Header>
                </Offcanvas> 

                ) : (
                    <Offcanvas show={show}  onHide={handleClose} placement='end'>
                    <Offcanvas.Header closeButton>
                        <Offcanvas.Title>Cart</Offcanvas.Title>
                            <Offcanvas.Body>
                               Conn√©cter vous
                            </Offcanvas.Body>
                    </Offcanvas.Header>
                </Offcanvas> 

                )
             


    return (

            {cartViewIsAuthenticated}
        
        
     )
}

export default CartItem
