import React, { useEffect, useState } from 'react'

import { Row, Col, Form} from 'react-bootstrap'

import { useSelector } from 'react-redux'
import { getUsersDetails, updateUsersProfile } from '../../../redux/actions/userActions'
import { USER_UPDATE_PROFILE_RESET } from '../../../redux/constants/userConstants'
import {  useNavigate } from "react-router-dom";
import { validEmail, validPassword } from '../../custom_validation.js';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Checkbox from '@mui/material/Checkbox';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Avatar from '@mui/material/Avatar';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import FormControlLabel from '@mui/material/FormControlLabel';
import TextField from '@mui/material/TextField';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import { ToastContainer, toast } from "react-toastify";
import { useDispatch } from "react-redux";
import { register } from "../../../redux/actions/userActions";



const ProfileUser = () => {

    const dispatch =  useDispatch()
    const navigate = useNavigate();
    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin
    const initialFormData = Object.freeze({
        email: userInfo.email,
        first_name: userInfo.first_name,
        last_name: userInfo.last_name,
        address: userInfo.address,
        phone: userInfo.phone,
        password:  userInfo.password,
        password2: userLogin.password2,
    })
    const [formData, setFormData] = useState(initialFormData)
    const [errorEmail, setErrorEmail] = useState(false)
    const [errorPassword, setErrorPassword] = useState(false)
    const [errorConfirmPassword, setErrorConfirmPassword] = useState(false)
    const userDetails = useSelector(state => state.userDetails)
    const { error, loading, user } = userDetails
   
    const userUpdateProfile = useSelector(state => state.userUpdateProfile)
    const { success } = userUpdateProfile
    const validateEmail = (email) => {
        
         if (!validEmail.test(email)) {
            setErrorEmail(true);
            console.log("error_email")
         }
    }
    const validatePassword = (password, password2) => {
      console.log(password)
       if (!validPassword.test(password && password2)) {
            setErrorPassword(true);
            console.log("error_password")
         }
       if (password != password2 ){
            setErrorConfirmPassword(true)
            console.log('error_confirm')
          
           
        }else{
          dispatch(updateUsersProfile({
                'email': formData.email,
                'first_name': formData.first_name, 
                'last_name': formData.last_name,
                'address': formData.address,
                'phone': formData.phone,
                'password':formData.password,},
               navigate('/')
          )) 
        }setErrorConfirmPassword('')
      
    }
        

        const handleSubmit = e => { 
            e.preventDefault(); 
            validateEmail(e.target[0].value)
            validatePassword(e.target[4].value,e.target[6].value )
            
        }

        const handleChange = e => {
          
            setFormData({
                ...formData,[e.target.id]: e.target.value,
              
            }) 
        } 


    useEffect(() => {
        if(!userInfo){
            navigate('/login')
        }else{
            if (!user || !user.first_name || success){
                dispatch({type: USER_UPDATE_PROFILE_RESET})
                dispatch(getUsersDetails('profile'))

            }else{
               //setFormData(first_name=user.first_name)
               //setFormData(email=user.email)
            
            }
        }
    }, [userInfo])

    const {email, first_name,last_name, address, phone, password, password2} = formData
    const theme = createTheme();
    return (
        <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="xs">
          <CssBaseline />
          <Box
            sx={{
              marginTop: 8,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Modifier profil
              {errorPassword ? <h3 style={{fontSize: '1rem', marginRight: '10px'}}>Le mot de passe doit contenir une majuscule un caractère spécial et au minimun 9 caractères</h3> : null }
              {errorEmail ? <h2 style={{fontSize: '1rem', marginRight: '10px'}}>Email incorrect</h2> : null}
              {errorConfirmPassword ? <h3 style={{fontSize: '1rem', marginRight: '10px'}}>Les mots de passe ne correspondent pas</h3> : null}
            </Typography>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    autoComplete
                    name="firstName"
                    required
                    fullWidth
                    id="first_name"
                    label="First Name"
                    autoFocus
                    onChange={handleChange}
                    defaultValue={userInfo.first_name}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="last_name"
                    label="Last Name"
                    name="lastName"
                    autoComplete="family-name"
                    onChange={handleChange}
                    defaultValue={userInfo.last_name}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    name="password"
                    label="Password"
                    type="password"
                    id="password"
                    autoComplete="new-password"
                    onChange={handleChange}
                    defaultValue={password}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    name="password2"
                    label="Password2"
                    type="password"
                    id="password2"
                    autoComplete="new-password"
                    onChange={handleChange}
                    defaultValue={password2}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="email"
                    label="Email Address"
                    name="email"
                    autoComplete="email"
                    onChange={handleChange}
                    defaultValue={email}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="phone"
                    label="phone"
                    name="phone"
                    autoComplete="phone"
                    onChange={handleChange}
                    defaultValue={userInfo.phone}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="address"
                    label="Address"
                    name="address"
                    autoComplete='off'
                    onChange={handleChange}
                    defaultValue={userInfo.address}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="address"
                    label="Address"
                    name="address"
                    autoComplete='off'
                    onChange={handleChange}
                    defaultValue={userInfo.postcode}
                  />
                </Grid>
               
              
                <Grid item xs={12}>
                  <FormControlLabel
                    control={<Checkbox value="allowExtraEmails" color="primary" />}
                    label="I want to receive inspiration, marketing promotions and updates via email."
                  />
                </Grid>
              </Grid>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
              >
                Sign Up
              </Button>
              <Grid container justifyContent="flex-end">
                <Grid item>
                  <Link href="/login" variant="body2">
                    Already have an account? Sign in
                  </Link>
                </Grid>
              </Grid>
            </Box>
          </Box>
         
        </Container>
      </ThemeProvider>
    )
}
export default ProfileUser
