import React, { Fragment, useEffect, useState } from "react"
import axios from 'axios';
import './welcome.css';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import CameraIcon from '@mui/icons-material/PhotoCamera';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Link from '@mui/material/Link';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import CircularProgress from '@mui/material/CircularProgress';
import { ToastContainer } from "react-toastify";
import { CartProvider, useCart } from "react-use-cart";



const Welcome = () => {



    const [showListProduct, setShowListProduct] = useState([])
    const [loader, setLoader] = useState(false)
  
    const {
      isEmpty,
      totalUniqueItems,
      items,
      updateItemQuantity,
      removeItem,
    } = useCart();
    

    const getAllProductFromApi = async() => {
        try {
            const res = await fetch 
                ('http://127.0.0.1:8000/api/product/')
                const repos = await res.json()
                setShowListProduct(repos)
               
            }  catch(error){
                    console.error(error)};
              
    }
        
    const CircularIndeterminated = () => {
         console.log('aa')
        return (
           
            <Box sx={{ display: 'flex' }}>
                <CircularProgress />
            </Box> 
        )

    }

 
   

   /// useEffect(() =>{
     
        ///localStorage.setItem('cart', JSON.stringify(cart))
       


  

    useEffect(() => {
        
      getAllProductFromApi()
      
        // eslint-disable-next-line react-hooks/exhaustive-deps
        }, []);


        const theme = createTheme();

  
        return (
            <ThemeProvider theme={theme}>
              <ToastContainer />
              <CssBaseline />
              <AppBar position="relative">
              <Toolbar>
                </Toolbar>
               
              </AppBar>
              <main>
                {/* Hero unit */}
                <Box
                  sx={{
                    bgcolor: 'background.paper',
                    pt: 8,
                    pb: 6,
                  }}
                >
                  <Container maxWidth="sm">
                  
                 
                    <Stack
                      sx={{ pt: 4 }}
                      direction="row"
                      spacing={2}
                      justifyContent="center"
                    >
                     
                    </Stack>
                  </Container>
                </Box>
                <Container sx={{ py: 8 }} maxWidth="md">
                  {/* End hero unit */}
                  <Grid container spacing={4}>
                    {showListProduct ? showListProduct.map((item) => (
                      <Grid item key={item.id} xs={12} sm={8} md={4}  >
                        <Card
                          sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                        >
                          <CardMedia
                            component="img"
                            sx={{
                              // 16:9
                              pt: '87.25%',
                            }}
                            src={item.image}
                           
                            alt="random"
                          />
                          <CardContent sx={{ flexGrow: 1 }}>
                            <Typography gutterBottom variant="h5" component="h2">
                              <h1>{item.titre}</h1>
                            </Typography>
                            <Typography>
                              <p>
                                {item.description}
                              </p>
                            </Typography>
                          </CardContent>
                          <CardActions>
                            <Button size="small">View</Button>
                            <Button onClick={() =>  updateItemQuantity(item.id, item.quantity + 1)} defaultValue={item.titre} size="small">Add</Button>
                          </CardActions>
                        </Card>
                      </Grid>
                    )) : ( 
                        
                        <Box sx={{ display: 'flex' }}>
                            <CircularProgress />
                        </Box>   )
                        }
                        
                  </Grid>
                </Container>
              </main>
              {/* Footer */}
              
              {/* End footer */}
            </ThemeProvider>

       
    );
}
export default Welcome;
