import React, { Fragment, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Button from 'react-bootstrap/Button';
import Offcanvas from 'react-bootstrap/Offcanvas';
import { IconButton } from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import '../NewCart/newcart.css'
import { Image, Row, Col, ListGroup, ListGroupItem, Form, Stack } from 'react-bootstrap';
import {Link,useNavigate}  from 'react-router-dom'
import { addToCart } from '../../redux/actions/cartActions';
import { removeItem } from '../../redux/actions/cartActions';


export const NewCart = () => {

    const cart = useSelector( state => state.cart)
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const { cartItems} = cart
    const [show, setShow] = useState(false);
    const productDetail = useSelector(state => state.productDetail.productDetail)
    const dataProduct = productDetail
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const removeTromCartHandler = (id) => {
    
        dispatch(removeItem(id))
          
      }
  
     const checkoutHandler = () => {
          navigate('/shipping')
      }
 
   

  return (
    <>
            
    <IconButton  
    id='icon-button'   onClick={handleShow} to=''>
    <ShoppingCartIcon  color='primary' fontSize='large'/>
    </IconButton>

 

      <Offcanvas placement='end' show={show} onHide={handleClose}>
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Panier</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
            <Stack gap={2}>
                {cartItems.map(item => 
                    <Fragment>
                    <div style={{display:'list-item' }} key={item.id}>
                        <Image id='imageProduct' alt="product" src={item.image} height={175} width={210}/>
                      
                    </div>

                    <div >{item.title}</div>

                  
                    
                  
                  
                  
                  
                    </Fragment>
                )}
            </Stack>
           
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
}
