import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import Button from 'react-bootstrap/Button';
import Offcanvas from 'react-bootstrap/Offcanvas';
import { IconButton } from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import '../NewCart/newcart.css'
import { Image, Row, Col, ListGroup, ListGroupItem, Form } from 'react-bootstrap';
import Link from 'react-router-dom'

export const NewCart = () => {

    const cart = useSelector( state => state.cart)
    
    const { cartItems} = cart
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
 
   

  return (
    <>
            
    <IconButton  
    id='icon-button'   onClick={handleShow} to=''>
    <ShoppingCartIcon  color='primary' fontSize='large'/>
    </IconButton>

 

      <Offcanvas placement='end' show={show} onHide={handleClose}>
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Panier</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
           <Row>
            <Col>
            
            {cartItems.length === 0 ? (<strong>Le panier est vide</strong>) : (
                    <ListGroup variant="flush">
                        {cartItems.map(item => 
                            <ListGroup.Item key={item.id}>
                                    <Row>
                                        <Col md={2}>
                                            <Image id='imageProduct' alt="product" src={item.image} height={175} width={210}/>
                                        </Col>
                                    
                                        <Col md={2}>
                                            <Link id="productTitle" to={`/product/${item.product}`}>{item.title}</Link>
                                         
                                        </Col>
                                        <Col md={2}>
                                             <p id="productPrice">{item.price.toFixed(2)}â‚¬</p>
                                        </Col>
                                        <Col md={2}>
                                            <Form.Control id="productQuantity"
                                                    as='select'
                                                    value={item.quantity}
                                                    onChange={(e) => dispatch(addToCart(item.product,Number( e.target.value)))}
