import { Offcanvas } from "react-bootstrap"
import React,{ Fragment, useEffect, useState} from 'react'

const CartItem = (handleClick) =>  {

    const {isOpen} = props 
    
    const [showCart, setShowCart] = useState(true)
    console.log(handleClick)
     
    const handleClose = () => setShowCart(false)
    //const handleShow  = () => setShow(isOpen)
    const getAuthenticationForCart = localStorage.getItem('access_token')
    

    const cartViewIsAuthenticated = showCart && getAuthenticationForCart ? (

                    <Offcanvas show={showCart}  onHide={handleClose} placement='end'>
                    <Offcanvas.Header closeButton>
                        <Offcanvas.Title>Cart</Offcanvas.Title>
                            <Offcanvas.Body>
                                Coucou
                            </Offcanvas.Body>
                    </Offcanvas.Header>
                </Offcanvas> 

                ) : (

                    <Offcanvas show={showCart}  onHide={handleClose} placement='end'>
                    <Offcanvas.Header closeButton>
                        <Offcanvas.Title>Cart</Offcanvas.Title>
                            <Offcanvas.Body>
                               Conn√©cter vous
                            </Offcanvas.Body>
                    </Offcanvas.Header>
                </Offcanvas> 

                ) 

    return (
        <Fragment>
            {cartViewIsAuthenticated}
        </Fragment>
        
     )
}

export default CartItem
