import React, { Fragment, useEffect, useRef, useState } from 'react'
import { useSelector } from 'react-redux';
import Logout from '../AccountUser/LogoutUser/logout';
import PersonIcon from '@mui/icons-material/Person';
import '../Navbar/navbar.css'
import LogoutIcon from '@mui/icons-material/Logout';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Login from '../AccountUser/LoginUser/Login';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { formControlLabelClasses, IconButton, Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import CartItem from '../CartItem/cartItem';
import { USER_LOGOUT } from '../../redux/constants/userConstants';
import ProfileUser from '../AccountUser/ProfileUser/profileUser';
import { NewCart } from '../NewCart/newcart';
import Button from '@mui/material/Button';






const NavigationBar = () => {

    const getUserData = useSelector(state => state.userLogin)
    const {userInfo} = getUserData
    const [show, setShow] = useState(false)

    //const [show, setShow] = useState(false);

    //const handleClose = () => setShow(false);
    //const handleShow = () => setShow(true);
    //const showCart = () => {
      
            //<NewCart />
    //}  
    const handleShow = () => setShow(true);


 
    return (

      <Navbar bg="light" expand="lg">
      <Container>
        <Navbar.Brand href="#home">Pure-Shop</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={Link} to={'/'}>Accueil</Nav.Link>
            <Nav.Link href="#link">Link</Nav.Link>
            <NavDropdown onMouseOver={() => handleShow()} show={false}  title={userInfo  ? userInfo.first_name : 'profil'}   id="basic-nav-dropdown">
            
             
              {userInfo ?  
                  <NavDropdown.Item  as={Link} to={'/profile'} >
                        Mon profil
                  </NavDropdown.Item> : (

                  <NavDropdown.Item disabled as={Link} to={'/profile'} >
                      Mon profil
                  </NavDropdown.Item>
                  
                )} 
                 <NavDropdown.Item  as={Link} to={'/password_reset'} >
                      Mot de passe
                  </NavDropdown.Item>

              {userInfo ?                    
                  <NavDropdown.Item  as={Link} to={'/order_history'} >
                    Mes commandes
                  </NavDropdown.Item> : (
                  
                
                  <NavDropdown.Item disabled as={Link} to={'/order_history'} >
                    Mes commandes
                  </NavDropdown.Item>
                  
                )}
  
              <NavDropdown.Item  >
                            <Logout/>
              </NavDropdown.Item>

            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
    
      

      <Typography fontSize='1.5em' fontFamily='' paddingRight='470px'></Typography>
      

      <IconButton component={Link} to="/login"> 
        <PersonIcon color='primary' fontSize='large'/>
      </IconButton>

      <IconButton component={NewCart} to=''>
        <ShoppingCartIcon color='primary' fontSize='large'/>
      </IconButton>
    
     </Container>
    </Navbar>
    )
}




export default NavigationBar;
