import React, { useState } from "react"
import { Link, useParams, Navigate, useNavigate} from 'react-router-dom'
import { useDispatch, useSelector } from "react-redux"
import { Col, Row, ListGroup, Image, Form, Button, Card, Figure, ListGroupItem } from "react-bootstrap"
import {addToCart, removeItem} from '../../redux/actions/cartActions'
import '../CartItem/cartItem.css'



const CartItems = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const {id} = useParams()
    //const [quantity, setQuantity] = useState(1)
    const cart = useSelector( state => state.cart)
    const productDetail = useSelector(state => state.productDetail.productDetail)
    const dataProduct = productDetail
    
    const { cartItems} = cart
    const removeTromCartHandler = (id) => {
    
      dispatch(removeItem(id))
        
    }

   // const checkoutHandler = () => {
        //navigate('/login')
    //}
     
    return (
        
        <Row>
            <Col md={8}>
                <h1>Panier</h1>

                {cartItems.length === 0 ? (<strong>Le panier est vide</strong>) : (
                    <ListGroup variant="flush">
                        {cartItems.map(item => 
                            <ListGroup.Item key={item.id}>
                                    <Row>
                                        <Col md={2}>
                                            <Image id='imageProduct' src={item.image} height={175} width={210}/>
                                        </Col>
                                    
                                        <Col md={2}>
                                            <Link id="productTitle" to={`/product/${item.product}`}>{item.title}</Link>
                                         
                                        </Col>
                                        <Col md={2}>
                                             <p id="productPrice">{item.price.toFixed(2)}€</p>
                                        </Col>
                                        <Col md={2}>
                                            <Form.Control id="productQuantity"
                                                    as='select'
                                                    value={item.quantity}
                                                    onChange={(e) => dispatch(addToCart(item.product,Number( e.target.value)))}
                                                    >
                                                        {
                                                            [...Array(dataProduct?.stock).keys()].map((x) => (
                                                                <option key={x+1} value={x+1}>
                                                                    {x+1}
                                                                </option>
                                                            ))
                                                        }

                                                        
                                                </Form.Control>
                                            
                                        </Col>
                                        <Col md={1}>
                                            <Button
                                                type='button'
                                                variant='light'
                                                onClick={() => removeTromCartHandler(item.product)}
                                                 

                                                >
                                                    <i className="fas fa-trash"></i>
                                            </Button>
                                        </Col>

                                       
                                    </Row>
                                     
                            </ListGroup.Item>
                          
                                )}
                      
                    </ListGroup>
                )}
            </Col>

            <Col md={3}>
                    <Card>
                        <ListGroup id="list-group" variant='flush'>
                                <ListGroup.Item id='detailCart'>
                                    <h2>Total article(s) {cartItems.reduce((acc, item) => acc + item.quantity, 0)}</h2>
                                    <h3>Montant total : {cartItems.reduce((acc, item) => acc  + item.price, 0).toFixed(2)} €</h3>
                                </ListGroup.Item>
                                <ListGroupItem>
                                    <Button  component={Link} to='/shipping'
                                        id="buttonCheckout"
                                        //onClick={() => checkoutHandler()}
                                        disabled={cartItems.length === 0  ? true : false}
                                        type="button"
                                        className="bnt-block"
                                      
                                        
                                        >Commander
                                        </Button>
                                </ListGroupItem>
                        </ListGroup>
                    </Card>
            </Col>
                
        </Row>
    )
}
export default CartItems
