import React , {useState} from "react"
import axiosInstance from "../../../axios";
import {  useNavigate } from "react-router-dom";
import { validEmail, validPassword } from './custom_validation.js';
import './registration.css'
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Checkbox from '@mui/material/Checkbox';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Avatar from '@mui/material/Avatar';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import FormControlLabel from '@mui/material/FormControlLabel';
import TextField from '@mui/material/TextField';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import { ToastContainer, toast } from "react-toastify";


const Registration = () => {

    const navigate = useNavigate();

    const initialFormData = Object.freeze({
        email: '',
        first_name: '',
        last_name: '',
        address: '',
        address2: '',
        phone:'',
        password: '',
        
    })

    const [formData, setFormData] = useState(initialFormData)
    const [errorEmail, setErrorEmail] = useState(false)
    const [errorPassword, setErrorPassword] = useState(false)



    const validateData = (email, password) => {

         if (!validEmail.test(email)) {
            setErrorEmail(true);
            console.log("error_email")
         }
         if (!validPassword.test(password)) {
            setErrorPassword(true);
            console.log("error_password")
         }
    }

    const handleChange = e => {
        
        console.log(e.target.value)
        setFormData({
            ...formData,[e.target.id]: e.target.value
        })
    }

    const handleSubmit = e => { 
        e.preventDefault();
       
        validateData(e.target[0].value, e.target[6].value)
        console.log('e')
      
        axiosInstance 
            .post(`http://127.0.0.1:8000/api/user/register/`, {
                email: formData.email,
                first_name: formData.first_name,
                last_name: formData.last_name,
                address: formData.address,
                address2: formData.address2,
                phone: formData.phone,
                password: formData.password

            })
             
        
            .then((res) => { 
              
                navigate('/')
              
               
            }) 
          


    }

    const sendWelcomeMsg = message =>  toast.success(`${message}`, {

      position: "top-right",
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
  }  );
    


    const {email, first_name,last_name, address, address2, phone, password} = formData
    const theme = createTheme();

   
  return (
    
    <Form onSubmit={handleSubmit}>
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign up
          </Typography>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  autoComplete="given-name"
                  name="firstName"
                  required
                  fullWidth
                  id="first_name"
                  label="First Name"
                  autoFocus
                  onChange={handleChange}
                  defaultValue={first_name}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  id="last_name"
                  label="Last Name"
                  name="lastName"
                  autoComplete="family-name"
                  onChange={handleChange}
                  defaultValue={last_name}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="new-password"
                  onChange={handleChange}
                  defaultValue={password}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                  onChange={handleChange}
                  defaultValue={email}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="phone"
                  label="phone"
                  name="phone"
                  autoComplete="phone"
                  onChange={handleChange}
                  defaultValue={phone}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="address"
                  label="Address"
                  name="address"
                  autoComplete='off'
                  onChange={handleChange}
                  defaultValue={address}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="address2"
                  label="Address2"
                  name="address2"
                  autoComplete='off'
                  onChange={handleChange}
                  defaultValue={address2}
                />
              </Grid>
            
              <Grid item xs={12}>
                <FormControlLabel
                  control={<Checkbox value="allowExtraEmails" color="primary" />}
                  label="I want to receive inspiration, marketing promotions and updates via email."
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Sign Up
            </Button>
            <Grid container justifyContent="flex-end">
              <Grid item>
                <Link href="#" variant="body2">
                  Already have an account? Sign in
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
       
      </Container>
    </ThemeProvider>
    </Form>
   

    )
    

     
}
export default Registration
