import React from "react"
import './Login.css'
import axiosInstance from "../../../axios";

const Login = () => {

    const history = useHistory();
    const initialFormData = Object.freeze({
        email: '',
        password: ''
    });
    const [formData, setFormData] = useState(initialFormData);

    const handleChange = e => {
        console.log(e.target.value)
        setFormData({
            ...formData,[e.target.id]: e.target.value
        })
    }

    const handleSubmit = e => {
        e.preventDefault();
      
      

        axiosInstance 
            .post(`http://127.0.0.1:8000/api/token/`, {
                email: formData.email,
                password: formData.password
            })
        
            .then((res) => {
                localStorage.getItem('access_token', res.data.access);
                localStorage.getItem('access_token', res.data.refresh);
                axiosInstance.defaults.headers['Authorization'] = 
                    'JWT' + localStorage.getItem('access_token');
                navigate('/welcome');
              
               
            })


    }


    return (
       
        <form onSubmit={handleSubmit}>
        <div class="form-group">
          <label for="exampleInputEmail1">Email address</label>
          <input Onchange={handleChange} type="email" class="form-control" id="email" aria-describedby="emailHelp" placeholder="Enter Username" required/>
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input  Onchange={handleChange} type="password" class="form-control" id="password" placeholder="Password" required/>
        </div>
        <div class="form-check">
          <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
          <label class="form-check-label" for="exampleCheck1">Check me out</label>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>


 
    )
}
export default Login;
