import React, { useState } from "react"
import { Link, useParams} from 'react-router-dom'
import { useDispatch, useSelector } from "react-redux"
import { Col, Row, ListGroup, Image, Form, Button, Card } from "react-bootstrap"
import {addToCart} from '../../redux/actions/cartActions'
import '../CartItem/cartItem.css'



const CartItems = () => {
    const {id} = useParams()
    //const [quantity, setQuantity] = useState(1)
    const cart = useSelector( state => state.cart)
    const productDetail = useSelector(state => state.productDetail.productDetail)
    const dataProduct = productDetail
    
    const { cartItems} = cart
     
    return (
        
        <Row>
            <Col md={8}>
                <h1>Shopping Cart</h1>

                {cartItems.length === 0 ? (<strong>Le panier est vide</strong>) : (
                    <ListGroup variant="flush">
                        {cartItems.map(item => 
                            <ListGroup.Item key={item.id}>
                                    <Row>
                                        <Col md={2}>
                                            <Image  id='imageProduct' src={item.image} alt={item.name} fluid rounded/>
                                        </Col>
                                        <Col md={3}>
                                            <Link id="productTitle" to={`/product/${item.product}`}>{item.title}</Link>
                                        </Col>
                                        <Col md={2}>
                                             ${item.price}
                                        </Col>
                                        <Col md={2}>
                                            <Form.Control
                                                    as='select'
                                                    value={item.quantity}
                                                    onChange={(e) => dispatch(addToCart(item.product, e.target.value))}
                                                    >
                                                        {
                                                            [...Array(dataProduct?.stock).keys()].map((x) => (
                                                                <option key={x+1} value={x+1}>
                                                                    {x+1}
                                                                </option>
                                                            ))
                                                        }

                                                        
                                                </Form.Control>
                                             {item.quantity}
                                        </Col>
                                    </Row>
                            </ListGroup.Item>
                                )}
                    </ListGroup>
                )}
            </Col>

            <Col md={4}>

            </Col>
                
        </Row>
    )
}
export default CartItems
