import { USER_LOGIN_FAIL,
     USER_LOGIN_REQUEST, 
     USER_LOGIN_SUCCESS, 
     USER_LOGOUT,

     USER_REGISTER_FAIL,
     USER_REGISTER_SUCCESS,
     USER_REGISTER_REQUEST,
     USER_DETAILS_REQUEST,
     USER_DETAILS_SUCCESS,
     USER_DETAILS_FAIL
    
} from "../constants/userConstants"

import axiosInstance from "../../axios";
import axios from "axios";


export const login = (email, password) => async(dispatch) => { 
    try {
        dispatch({
        type: USER_LOGIN_REQUEST
    })
    const {data} = await  axiosInstance.post(`http://127.0.0.1:8000/api/token/`, {
        email: email,
        password: password
        })
     
        //.then((res) => {
            //console.log(res)
            localStorage.setItem('access_token', data.access);
            localStorage.setItem('refresh_token', data.refresh);   
            axiosInstance.defaults.headers['Authorization'] = 
                'JWT' + localStorage.getItem('access_token'); 
              
         //}  //)
       
       dispatch({
            type: USER_LOGIN_SUCCESS,
            payload: data 
           
       }
      )

    } catch(error){
       
        dispatch({
            type: USER_LOGIN_FAIL,
            error: error.response.data.detail //&& error.response.data.detail
           // ? error.response.data.detail : error.message
        })
    }
}

export const logout = () => async(dispatch) => {
    try{
        dispatch({
            type: USER_LOGOUT,
        })
        const response = axiosInstance.post('http://127.0.0.1:8000/api/user/logout/blacklist/', {
            refresh_token : localStorage.getItem('refresh_token') , 
            
        });
            localStorage.removeItem('access_token');
            localStorage.removeItem('refresh_token');
        
            axiosInstance.defaults.headers['Authorization'] = null;
        
    }catch(error){
        console.log(error)
    }
}


export const register = (email, 
                        first_name,
                        last_name,
                        address,
                        phone,
                        password ) => async(dispatch) => { 
    try {
        dispatch({
        type: USER_REGISTER_REQUEST
    })
    const {data} = axiosInstance 
    .post(`http://127.0.0.1:8000/api/user/register/`, {
        email: email,
        first_name: first_name,
        last_name: last_name,
        address: address,
        phone: phone,
        password: password

    })
       
       dispatch({
            type: USER_REGISTER_SUCCESS,
            payload: data 
           
       }
      )

    } catch(error){
       console.log(error)
        dispatch({
            
            type: USER_REGISTER_FAIL,
            error: error.response.data //&& error.response.data.detail
           // ? error.response.data.detail : error.message
        })
    }
}

export const getUsersDetails =  (id) => async(dispatch, getState) => {
    
    try{
        dispatch({ type: USER_DETAILS_REQUEST})
        const {userLogin: {userInfo},} = getState()
        console.log(id)
       
        const config = {
            headers: {
                'Content-type':  'application/json',
                Authorization: `Bearer ${userInfo.token}`
            }

        }

        const {data} = axios.get(`http://127.0.0.1:8000/api/user/${id}/`,  config )
     



        dispatch({
            type: USER_DETAILS_SUCCESS,
            payload: data
        })


    }catch(error){
        dispatch({
            type : USER_DETAILS_FAIL
          
        })
    }


}
