import { CART_ADD_ITEM } from "../../constants/cartConstants";

export const cartReducer = (state ={ cartItems : [] },action) => {
    switch(action.type){
        case CART_ADD_ITEM:
            const item = action.payload
           
            const existItem = state.cartItems.find((x) => x === item.product)
            console.log(existItem)
                    
            if (!existItem){
                console.log('ee')
                return {
                    ...state,
                     cartItems: state.cartItems.quantity += item.quantity}

                   
            }else {
                return{
                    ...state,
                    cartItems: [...state.cartItems, item]
                }
            } 
            default: 
                return state
        }
        
    
}
