import React, {useEffect, useState} from "react"
import './Login.css'
import { toast } from 'react-toastify';
import axiosInstance from "../../../axios";
import {  useNavigate } from "react-router-dom";



const Login = () => {

    const navigate = useNavigate();
    const initialFormData = Object.freeze({
        email: '',
        password: ''
    });
    const [formData, setFormData] = useState(initialFormData);
    const [showWelcomeMsg ,setShowWelcomeMsg] = useState(false)

    const handleChange = e => {
       
        setFormData({
            ...formData,[e.target.id]: e.target.value
        })
    }

    const handleSubmit = e => {
        e.preventDefault();
      
      

        axiosInstance 
            .post(`http://127.0.0.1:8000/api/token/`, {
                email: formData.email,
                password: formData.password
            })
        
            .then((res) => {
                localStorage.setItem('access_token', res.data.access);
                localStorage.setItem('refresh_token', res.data.refresh);
                axiosInstance.defaults.headers['Authorization'] = 
                    'JWT' + localStorage.getItem('access_token');
                navigate('/');
              
               
            })


    }



    useEffect(() => {
         
        if (formData.email){
            WelcomeMsg(formData.email)
            console.log("Okk")
          


        }
    })
      
 
       const WelcomeMsg = pseudo => {
            if(showWelcomeMsg){
                setShowWelcomeMsg(true)
                const name = localStorage.getItem('name')
                console.log("Ok")
            }
                
            
                toast.warn(`Welcome ${pseudo}`, {
                    position: "top-right",
                    autoClose: 3000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
          
        });
    
        
    }

    const {email, password}  = formData

    return (
       
        <form onSubmit={handleSubmit}>
        <div class="form-group">
          <label for="exampleInputEmail1">Email address</label>
          <input onChange={handleChange} defaultValue={email} type="email" class="form-control" id="email" aria-describedby="emailHelp" placeholder="Enter Username" required/>
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input  onChange={handleChange} defaultValue={password} type="password" class="form-control" id="password" placeholder="Password" required/>
        </div>
        <div class="form-check">
          <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
          <label class="form-check-label" for="exampleCheck1">Check me out</label>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>


 
    )
}
export default Login;
