import { maxWidth } from "@mui/system";

import axios from "axios"
import { CreatePayment } from "../../redux/actions/paymentAction";
import React, { useState, useEffect,  } from "react";
import { API_URL } from "../Stripe/StripeContainer";
import { useDispatch, useSelector } from "react-redux";
import { Row ,Col, ListGroup, Image, Button} from "react-bootstrap";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";


const Payment = () => {

    const stripe = useStripe()
    const elements = useElements()
    const [message, setMessage] = useState("");
    const cart = useSelector(state => state.cart)
    const {cartItems} = cart
    console.log(cartItems[0].title)
   
    const orderCreate = useSelector(state => state.orderCreate)
    const {orderUser} = orderCreate
    const totalPrice = orderUser?.[0]?.totalPrice
   
    const dispatch = useDispatch()

    const handleSubmit = async(event) =>{
        event.preventDefault()
        const {error, paymentMethod} = await stripe.createPaymentMethod({
            type: "card",
            card: elements.getElement(CardElement),
        });
        if(!error){
            console.log("token genere :", paymentMethod)
            try{
                const {id} = paymentMethod;
                const response = await axios.post(`http://127.0.0.1:8000/api/stripe/charge/`,{
                    amount: totalPrice,
                    id: id,
                });
                if (response.data.success)
                    console.log('payment reussi')
                }
            catch(error){
                   console.log('payment pas reussi')
             
            }
        }
    }
   
    

   


    return(

        <form onSubmit={handleSubmit} style={{maWidth: 400}}>
            <CardElement
                options={{
                    hidepostalCode: true,
                }}
            />
            <button>Payer</button>
        </form>

    )
}

export default Payment
