import { PAYMENT_USER_FAIL, PAYMENT_USER_REQUEST } from "../constants/paymentConstants"

export const CreatePayment =  () => async(dispatch, getState) => {
    
    try{
        dispatch({ type: PAYMENT_USER_REQUEST})
        const {userLogin: {userInfo},} = getState()
      
       
        const config = {
            headers: {
                'Content-type':  'application/json',
                Authorization: `Bearer ${userInfo.token}`
            }

        }
        console.log(config)

        const {data} = await axios.post(`http://127.0.0.1:8000/api/stripe/create-checkout-session/`,  config )


        dispatch({
            type: PageTransitionEvent,
            payload: data
        })

      

       



    }catch(error){
        dispatch({
            type : PAYMENT_USER_FAIL,
            payload : error.response
          
        })
    }


}
