import React , {useState} from "react"
import axiosInstance from "../../../axios";


const Registration = () => {

    const initialFormData = ({
        email: '',
        username: '',
        password: ''
    })

    const [formData, setFormData] = useState(initialFormData)

    const handleChange = e => {
        console.log(e.target.value)
        setFormData({
            ...formData,[e.target.id]: e.target.value
        })
    }

    const handleSubmit = e => {
        e.preventDefault();
        console.log(formData)

        axiosInstance 
            .post(`user/register`, {
                email: formData,
                username: formData.username,
                password: formData.password
            })
            .then((res) => {
                history.pushState('/welcome')
               
            })


    }
    


    const {email, username, password} = formData

    return (
       
              
        <form onSubmit={handleSubmit}>
            <div class="form-group">
                <label htmlFor="InputEmail1">Email address</label>
                <input onChange={handleChange} type="email" class="form-control" defaultValue={email} id="InputEmail" aria-describedby="emailHelp" placeholder="Enter Username" required/>
                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
            </div>
            <div class="form-group">
                <label htmlFor="InputUsername">Username</label>
                <input onChange={handleChange} type="name" class="form-control" defaultValue={username} id="InputUSername" placeholder="Password" required/>
            </div>
            <div class="form-group">
                <label htmlFor="InputPassword1">Password</label>
                <input onChange={handleChange} type="password" class="form-control" defaultValue={password} id="InputPassword" placeholder="Password" required/>
            </div>
                <div class="form-check">
                <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
            <label class="form-check-label" for="exampleCheck1">Check me out</label>
            </div>
            <button type="submit" class="btn btn-primary">Submit</button>
        </form>

    )
}
export default Registration
