import { PAYMENT_USER_FAIL, PAYMENT_USER_REQUEST, PAYMENT_USER_SUCCESS } from "../constants/paymentConstants"
import axios from "axios"
import { API_URL } from "../../components/Stripe"

export const CreatePayment =  (cartItems, totalPrice) => async(dispatch, getState) => {
     console.log(cartItems, totalPrice)
    try{
        dispatch({ type: PAYMENT_USER_REQUEST})
        const {userLogin: {userInfo},} = getState()
       
       
        const config = {
            headers: {
                'Content-type':  'application/json',
                Authorization: `Bearer ${userInfo.token}`
            }

        }
       
       
        const {data} = await axios.post('http://127.0.0.1:8000/api/stripe/create-checkout-session/', cartItems, totalPrice, config )
        console.log(data)

        dispatch({
            type: PAYMENT_USER_SUCCESS,
            payload: data
        })

      

       



    }catch(error){
        dispatch({
            type : PAYMENT_USER_FAIL,
            payload : error.response
          
        })
    }


}
