import { USER_ORDER_REQUEST,
        USER_ORDER_SUCCESS,
        USER_ORDER_FAIL,
        USER_ORDER_EMPTY,

        ORDER_PAY_SUCCESS,
        ORDER_PAY_REQUEST,
        ORDER_PAY_FAIL,
        ORDER_PAY_EMPTY,

        ORDER_DETAILS_REQUEST,
        ORDER_DETAILS_SUCCESS,
        ORDER_DETAILS_FAIL,
        ORDER_DETAILS_EMPTY


} from "../../constants/orderConstants";


export const OrderReducer = (state={},  action ) => {
    switch (action.type) {
        case USER_ORDER_REQUEST:
            return{loading: true}
            
        case USER_ORDER_SUCCESS:
            return { loading:false,success:true, orderUser:[action.payload ]}
            ;
        case USER_ORDER_FAIL:
            return { loading:true, error:action.payload}

        case USER_ORDER_EMPTY:
            return {}
    
        default:
            return state;
    }
}


export const OrderPaidReducer = (state={},  action ) => {
    switch (action.type) {
        case ORDER_PAY_REQUEST:
            return{loading: true}
            
        case ORDER_PAY_SUCCESS:
            return { loading:false,success:true}
            ;
        case ORDER_PAY_FAIL:
            return { loading:true, error:action.payload}

        case ORDER_PAY_EMPTY:
            return {}
    
        default:
            return state;
    }
}

export const OrderDetailsReducer = (state={loading:true, orderItems:[]},  action ) => {
    switch (action.type) {
        case ORDER_DETAILS_REQUEST:
            return{loading: true}
            
        case ORDER_DETAILS_SUCCESS:
            return { loading:false,success:true}
            ;
        case ORDER_DETAILS_FAIL:
            return { loading:true, error:action.payload}

        case ORDER_DETAILS_EMPTY:
            return {}
    
        default:
            return state;
    }
}
