import React, {useState, useEffect} from "react"
import { Row, Col,Image,ListGroup, Button, ListGroupItem } from "react-bootstrap"
import { useBootstrapBreakpoints } from "react-bootstrap/esm/ThemeProvider"
import {Link, useParams} from "react-router-dom"
import '../Products/productDetail.css'

import Products from "../Products/product"
import { Rating } from "@mui/material"

const ProductDetail = () => {
   
    const [product, setProduct] = useState([])
    const{id} = useParams()
   
    
   
  
    useEffect(() => {
        try {
            async function getProductFromApi(){

                const res = await fetch
                (`http://127.0.0.1:8000/api/product/${id}`)
                const repos = await res.json()
              
                setProduct(repos)
             
            }
             
            getProductFromApi()
               
            } catch(error){
                    console.error(error)};
       
         
    }, []
    )

    

    
    return(
        <div>

            <Link to='/' className="btn btn-light my-3">Retour</Link>
            <Row>
                <Col  md={5}>
                    <Image src={product.image} alt={product.titre} fluid />
                </Col>
                <Col md={3}>
                    <ListGroup varinat="flush">
                       <ListGroup.Item>
                            <h1>{product.title}</h1>
                       </ListGroup.Item>
                        <ListGroup.Item>
                             <Rating></Rating>
                       </ListGroup.Item>
                       <ListGroup.Item>
                             <h3>{product.price}</h3>
                       </ListGroup.Item>
                      
                       <ListGroup.Item>
                          <h2>{product.description}</h2>
                       </ListGroup.Item>
                    </ListGroup>
                </Col>
                <Col md={3}></Col>
            </Row>
      
        </div>
    )
}
export default ProductDetail
