import { maxWidth } from "@mui/system";

import axios from "axios"
import { CreatePayment } from "../../redux/actions/paymentAction";
import React, { useState, useEffect,  } from "react";
import { API_URL } from "../Stripe/StripeContainer";
import { useDispatch, useSelector } from "react-redux";
import { Row ,Col, ListGroup, Image, Button, FormControl} from "react-bootstrap";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";


const Payment = () => {

    const stripe = useStripe()
    const elements = useElements()
    const [message, setMessage] = useState("");
    const cart = useSelector(state => state.cart)
    const {cartItems} = cart
  
   
    const orderCreate = useSelector(state => state.orderCreate)
    const {orderUser} = orderCreate
    const totalPrice = orderUser?.[0]?.totalPrice
    const userLogin = useSelector(state => state.userLogin)
    const {userInfo} =  userLogin
    const dispatch = useDispatch()

    useEffect(() => {
        // Check to see if this is a redirect back from Checkout
        const query = new URLSearchParams(window.location.search);
    
        if (query.get("success")) {
          setMessage("Order placed! You will receive an email confirmation.");
        }
    
        if (query.get("canceled")) {
          setMessage(
            "Order canceled -- continue to shop around and checkout when you're ready."
          );
        }
      }, []);

 
 return (
       
    <Col md={8}>  
             

                    <ListGroup variant="flush">
                        {cartItems.map(item => 
                            <ListGroup.Item key={item.id}>
                                    <Row>
                                        <Col md={2}>
                                            <Image id='imageProduct' src={item.image} height={175} width={210}/>
                                        </Col>
                                    
                                        <Col md={2}>
                                            <Link id="productTitle" to={`/product/${item.product}`}>{item.title}</Link>
                                         
                                        </Col>
                                        <Col md={2}>
                                             <p id="productPrice">{item.price.toFixed(2)}â‚¬</p>
                                        </Col>
                                        <Col md={2}>
                                           
                                            
                                        </Col>
                                        <Col md={1}>
                                            <FormControl 
                                            action='create-checkout-session/'
                                            method='POST'
                                            
                                            >
                                                <Button
                                                type="submit"
                                                >Checkout

                                                </Button>
                                                
                                            </FormControl>
                                          
                                        </Col>

                                       
                                    </Row>
                                     
                            </ListGroup.Item>
                          
                                )}
                      
                    </ListGroup>
               
            </Col>
  
 





 


   )}

   

export default Payment
