import { maxWidth } from "@mui/system";
import {CardElement, useElements, useStripe } from "@stripe/react-stripe-js";
import axios from "axios"
import { CreatePayment } from "../../redux/actions/paymentAction";
import React, { useState, useEffect,  } from "react";
import { API_URL } from "../Stripe";
import { useDispatch, useSelector } from "react-redux";


const Payment = () => {
    const [message, setMessage] = useState("");
    const orderCreate = useSelector(state => state.orderCreate)
    const {orderUser} = orderCreate
    const getTotalPrice = orderUser
    console.log(orderUser.totalPrice)
    const dispatch = useDispatch()

    const handlePayment = e => {  
        e.preventDefault();
        dispatch(CreatePayment())
    }

    useEffect(() => {

        const query = new URLSearchParams(window.location.search)
        if (query.get("success")) {
            setMessage("Order placed! You will receive an email confirmation.");
          }
      
        if (query.get("canceled")) {
            setMessage(
              "Order canceled -- continue to shop around and checkout when you're ready."
            );
          }
        }, []);
    


    return(
        <section>
            <div className="product">
            <img
                src="https://i.imgur.com/EHyR2nP.png"
                alt="The cover of Stubborn Attachments"
            />
            <div className="description">
            <h3>Stubborn Attachments</h3>
            <h5>$20.00</h5>
            </div>
            </div>
            <form >
              
                <button onClick={() => handlePayment()} type="submit">
                    Checkout
            </button>
            </form>
        </section>
       );

    }

export default Payment
