import React, { Fragment, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Button from 'react-bootstrap/Button';
import Offcanvas from 'react-bootstrap/Offcanvas';
import { IconButton } from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import '../NewCart/newcart.css'
import { Image, Row, Col, ListGroup, ListGroupItem, Form, Stack, CloseButton } from 'react-bootstrap';
import {Link,useNavigate}  from 'react-router-dom'
import { removeItem } from '../../redux/actions/cartActions';
import Badge from '@mui/material/Badge';
//import { styled } from '@mui/material/styles';
import styled from '@emotion/styled';
import { addToCart } from '../../redux/actions/cartActions';




export const NewCart = () => {

    const cart = useSelector( state => state.cart)
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const { cartItems} = cart
    const [show, setShow] = useState(false);
    const productDetail = useSelector(state => state.productDetail.productDetail)
    const dataProduct = productDetail
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
 

    const removeTromCartHandler = (id) => {
    
        dispatch(removeItem(id))
          
      }
  
     const checkoutHandler = () => {
          navigate('/shipping')
      }
      
      const EditButton = styled(IconButton)`
          outline: none;
          border: none;
        `;
   

  return (
    <>
            
    <IconButton    id='icon-button' aria-label='cart'  onMouseOver={handleShow} to=''> 
        <Badge badgeContent={cartItems.length} color='secondary'>
          <ShoppingCartIcon  color='primary' fontSize='large'/>
        </Badge>
    </IconButton>

 

      <Offcanvas placement='end' show={show} onHide={handleClose}>
        <Offcanvas.Header closeButton>
          <Offcanvas.Title style={{marginLeft:'20px'}}>Panier</Offcanvas.Title>

        </Offcanvas.Header>
        <Offcanvas.Body>
            <Stack gap={1} >
                {cartItems.map(item => 
                  
                  <Row xs={'auto'} md={5}>
                    <Col xs={2} id='column-product' key={item.id}>
                        
                            <CloseButton
                                id='closeButton'
                                type='button'
                                onClick={() => removeTromCartHandler(item.product)}
                                >
                            </CloseButton>
                            
                        <Col>
                            <Image id='imageProduct' alt="product" src={item.image} height={175} width={210}/>
                           
                        </Col>
                        <Col md={2} >
                        <h2 id='productTitle'>{item.title}</h2>
                       
                        </Col>
                        <Col>
                            <h2 id='productTitle'>{item.price}€</h2>
                           
                           
                        </Col>
                        <Form.Control id="productQuantity"
                            as='select'
                            value={item.quantity}
                            onChange={(e) => dispatch(addToCart(item.product,Number( e.target.value)))}
                            >
                                {
                                    [...Array(dataProduct?.stock).keys()].map((x) => (
                                        <option key={x+1} value={x+1}>
                                            {x+1}
                                        </option>
                                    ))
                                }                                                      
                        </Form.Control>
                       <Col>
                      
                                            
                        </Col>

                             </Col>               
                  
                    <hr></hr>
                   </Row>
                    
                  
                  
                  
                  
                   

                )}
            </Stack> 
            
            <h3>Montant total : {cartItems.reduce((acc, item) => acc  + item.price, 0).toFixed(2)}€</h3>
         
              <Button variant='primary' size='md' component={Link} to='/shipping'
                    id="buttonCheckout"
                    onClick={() => checkoutHandler()}
                    disabled={cartItems.length === 0  ? true : false}
                    type="button"
                    className="bnt-block"                                   
                    >Commander
              </Button>
             
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
}
