import { maxWidth } from "@mui/system";

import axios from "axios"
import { CreatePayment } from "../../redux/actions/paymentAction";
import React, { useState, useEffect,  } from "react";
import { API_URL } from "../Stripe/StripeContainer";
import { useDispatch, useSelector } from "react-redux";
import { Row ,Col, ListGroup, Image, Button, FormControl, Form} from "react-bootstrap";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { Link } from "react-router-dom";

const Payment = () => {

    const stripe = useStripe()
    const elements = useElements()
    const [message, setMessage] = useState("");
    const cart = useSelector(state => state.cart)
    const {cartItems} = cart
  
   
    const orderCreate = useSelector(state => state.orderCreate)
    const {orderUser} = orderCreate
    const name = orderUser?.[0]?.user.email
    console.log(name)
    const totalPrice = orderUser?.[0]?.totalPrice
    const userLogin = useSelector(state => state.userLogin)
    const {userInfo} =  userLogin
    const dispatch = useDispatch()
    const [error, setError] = useState(null);
    const [email, setEmail] = useState('');

    
    const config = {
        headers: {
            'Content-type':  'application/json',
            Authorization: `Bearer ${userInfo.token}`
        }

    }
    console.log(config)

    const handleChange = (event) => {
        if (event.error) {
          setError(event.error.message);
        } else {
          setError(null);
        }
      }
      // Handle form submission.
  

    const handleSubmit = async(event) =>{
        event.preventDefault()
        console.log(event)
        const {error, paymentMethod} = await stripe.createPaymentMethod({
            type: "card",
            card: elements.getElement(CardElement),
        });
        console.log(error)
        if(!error){
            console.log("token genere :", paymentMethod)
            try{
                const {id} = paymentMethod;
                console.log(id)
                const response = await axios.post('http://127.0.0.1:8000/api/stripe/payment/',config,{
                    amount: totalPrice,
                    id: id,
                    
                  
                });
                console.log(response)
                if (response.data.success)
                    console.log('payment reussi')
                }
            catch(error){
                   console.log(error)
             
            }
        }
    }
   
    

   


    return(

        <form onSubmit={handleSubmit} className="stripe-form">
    <div className="form-row">
      <label htmlFor="email">Email Address</label>
      <input className="form-input" id="email" name="name"    type="email" placeholder="jenny.rosen@example.com" required 
value={email} onChange={(event) => { setEmail(event.target.value)}} />
    </div>
    <div className="form-row">
      <label for="card-element">Credit or debit card</label> 
      <CardElement id="card-element" onChange={handleChange}/>
      <div className="card-errors" role="alert">{error}</div>
    </div>
    <button type="submit" className="submit-btn">
      Submit Payment
    </button>
  </form>

    )


 
  
 





 


            }

   

export default Payment
