import React , {useState} from "react"
import axiosInstance from "../../../axios";
import { useHistory } from "react-router-dom";


const Registration = () => {

    const history = useHistory()

    const initialFormData = ({
        email: '',
        first_name: '',
        password: ''
    })

    const [formData, setFormData] = useState(initialFormData)

    const handleChange = e => {
        console.log(e.target.value)
        setFormData({
            ...formData,[e.target.id]: e.target.value
        })
    }

    const handleSubmit = e => {
        e.preventDefault();
        console.log()
      
      

        axiosInstance 
            .post(`http://127.0.0.1:8000/api/user/register/`, {
                email: formData.email,
                first_name: formData.first_name,
                password: formData.password
            })
        
            .then((res) => {
                history.push('/welcome')
                console.log(res.data)
               
            })


    }
    


    const {email, first_name, password} = formData

    return (
       
              
        <form onSubmit={handleSubmit}>
            <div class="form-group">
                <label htmlFor="InputEmail1">Email address</label>
                <input onChange={handleChange} type="email" class="form-control" defaultValue={email}  autoComplete="off" id="email" aria-describedby="emailHelp" placeholder="Enter Username" required/>
                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
            </div>
            <div class="form-group">
                <label htmlFor="InputUsername">first_name</label>
                <input onChange={handleChange} type="name" class="form-control" defaultValue={first_name}  autoComplete="off" id="first_name" placeholder="Password" required/>
            </div>
            <div class="form-group">
                <label htmlFor="InputPassword1">Password</label>
                <input onChange={handleChange} type="password" class="form-control" defaultValue={password}  autoComplete="off" id="password" placeholder="Password" required/>
            </div>
                <div class="form-check">
                <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
            <label class="form-check-label" for="exampleCheck1">Check me out</label>
            </div>
            <button type="submit" class="btn btn-primary">Submit</button>
        </form>

    )
}
export default Registration
