import React, { useEffect } from "react"
import { Col, Row, ListGroup, ListGroupItem } from "react-bootstrap"
import { useDispatch, useSelector } from "react-redux"
import { getOrderDetails } from "../../../redux/actions/orderActions"
import { Navigate, useParams } from "react-router-dom"
import { getOrderList } from "../../../redux/actions/orderActions"
import { USER_UPDATE_PROFILE_RESET } from "../../../redux/constants/userConstants"
import { getUsersDetails } from "../../../redux/actions/userActions"

export const OrderHistory = () => {

    const navigate = Navigate
    const dispatch = useDispatch()
    const {id} = useParams()
    
   
    const userlogin = useSelector(state => state.userLogin)
    const {userInfo} = userlogin
    const userUpdateProfile =  useSelector(state => state.userUpdateProfile)
    const { success} = userUpdateProfile
    const OrderListMyReducer = useSelector(state => state.OrderListMyReducer)
    const {loading , order} = OrderListMyReducer
    console.log(order)
    useEffect(() => {
        if(!userInfo){
            navigate('/login')
        } else {
           
                dispatch({type: USER_UPDATE_PROFILE_RESET})
                
                dispatch(getOrderList())
            }
    }
    , [userInfo])
    


    return (
            <div>
                <Row>
                    <Col>
                        <ListGroup variant="flush">
                            <ListGroupItem>
                                <h2>Mes commandes</h2>
                                <p>
                                    <strong></strong>
                                    {userInfo.address}, {userInfo.city}
                                    {'  '}
                                    {userInfo.postcode}
                                    {'  '}
                                </p>
                            </ListGroupItem>

                        </ListGroup>

                    </Col>
                </Row>

            </div>
    )

}
